const delay = (ms: number) => {
  return new Promise((resolve) => setTimeout(resolve, ms));
};

const swap = (arr: number[], a: number, b: number): void => {
  const temp = arr[a];
  arr[a] = arr[b];
  arr[b] = temp;
};

// пузырек
export const bubbleSort = async(arr: number[], direction: string) => {
  if (direction === "по возрастанию") {
    for (let j = arr.length - 1; j > 0; j--) {
      for (let i = 0; i < j; i++) {
        if (arr[i] > arr[i + 1]) {
          swap(arr, i, i + 1);
        }
      }
    }         

  } else if (direction === "по убыванию") {
    for (let j = arr.length - 1; j > 0; j--) {
      for (let i = 0; i < j; i++) {
        if (arr[i] < arr[i + 1]) {
          swap(arr, i, i + 1);
        }
      }
    }
  }
  console.log(arr);
  return arr;
};

// выбор
export const selectionSort = (arr: number[], direction: string) => {
  const { length } = arr;
  if (direction === "по убыванию") {
    for (let i = 0; i < length - 1; i++) {
      let maxInd = i;
      for (let j = i; j < length; j++) {
        if (arr[maxInd] < arr[j]) {
          maxInd = j;
        }
      }
      if (i !== maxInd) {
        swap(arr, i, maxInd);
      }
    }
  } else if (direction === "по возрастанию") {
    for (let i = 0; i < length - 1; i++) {
      let minInd = i;
      for (let j = i; j < length; j++) {
        if (arr[minInd] > arr[j]) {
          minInd = j;
        }
      }
      if (i !== minInd) {
        swap(arr, i, minInd);
      }
    }
  }

  console.log(arr);
  return arr;
};
