import { Dispatch, SetStateAction } from "react";
import { list } from "../components/list-page/List";
import { IArrEl } from "../utils/utils";
import {ElementStates} from '../types/element-states'

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps<T> {
  array?: T[];
  value?: string;
  index?: string;
  setArray?: Dispatcher<T[]>;
  setHead?: Dispatcher<number>;
  setIsLoader?: Dispatcher<boolean>;
  setIsDisabled?: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<boolean>;
  setTail?: Dispatcher<number>;
}

export const addHead = ({
  array,
  value,
  setIsLoader,
  setIsDisabled,
  setHead,
  setIsChanging,
  setTail,
  setArray,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  setHead!(-1);
  setIsChanging!(true);
  const newEl = {number: Number(value), state: ElementStates.Default}
  list.unshift(newEl, array!);
  setArray!(list.print());
  setIsChanging!(false);
  setHead!(list.head);
  setTail!(list.tail);
  setIsLoader!(false);
  setIsDisabled!(false);
};

export const addTail = ({ array, value, setIsLoader, setTail }: IProps) => {
  if (value) {
    setIsLoader!(true);
    const newEl = {number: Number(value), state: ElementStates.Default}
    list!.push(newEl);
    setTail!(list!.length - 1);
  } else {
    alert("введите значение в инпут");
  }
  setIsLoader!(false);
};

export const removeHead = ({ list, setIsLoader, setHead, setTail }: IProps) => {
  setIsLoader!(true);
  list!.shift();
  setHead!(0);
  setTail!(list!.length - 1);
  setIsLoader!(false);
};

export const removeTail = ({ list, setIsLoader, setTail }: IProps) => {
  setIsLoader!(true);
  list!.pop();
  setTail!(list!.length - 1);
  setIsLoader!(false);
};

export const addIndex = ({
  list,
  value,
  index,
  setIsLoader,
  setHead,
  setTail,
}: IProps) => {
  if (value && index) {
    list!.splice(Number(index), 0, value);
    setIsLoader!(false);
    setHead!(0);
    setTail!(list!.length - 1);
  } else {
    alert("Введите индекс и число");
  }
};

export const removeIndex = ({
  list,
  index,
  setIsLoader,
  setHead,
  setTail,
}: IProps) => {
  if (index) {
    setIsLoader!(true);
    list!.splice(Number(index), 1);
    setIsLoader!(false);
    setHead!(0);
    setTail!(list!.length - 1);
  } else {
    alert("Введите индекс");
  }
};
