import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";
import { stack } from "../components/stack-page/stack_class";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  currentIndex?: number;
  setIsDisabled: Dispatcher<boolean>;
  setIsLoaderAdd?: Dispatcher<boolean>;
  setIsLoaderRemove?: Dispatcher<boolean>;
  setIsLoaderClear?: Dispatcher<boolean>;
  setValue?: Dispatcher<string>;
  setArray: Dispatcher<string[]>;
  setCurrentIndex?: Dispatcher<number>;
}

export const pushStack = async ({
  value,
  setIsDisabled,
  setIsLoaderAdd,
  setArray,
  setCurrentIndex,
  currentIndex,
  setValue,
}: IProps) => {
  setIsLoaderAdd!(true);
  setIsDisabled(tr)
  stack.push(value!);
  setArray(stack.printStack());
  await delay(500);
  setCurrentIndex!(currentIndex! + 1);
  setValue!("");
  setIsLoaderAdd!(false);
};

export const popStack = async ({
  setIsDisabled,
  setIsLoaderRemove,
  setArray,
  setCurrentIndex,
}: IProps) => {
  setIsLoaderRemove!(true);
  await delay(500);
  stack.pop();
  setArray(stack.printStack());
  setCurrentIndex!(stack.getSize() - 1);
  setIsLoaderRemove!(false);
};

export const clearStack = async ({ setIsDisabled, setIsLoaderClear, setArray }: IProps) => {
  setIsLoaderClear!(true);
  await delay(500);
  stack.clear();
  setArray(stack.printStack());
  setIsLoaderClear!(false);
};
