export const fibonacci = async (n: number, setResult, setIsLoader, setValue) => {
    if (n >= 1 && n <= 19) {
      let arr = [0, 1];
      for (let i = 2; i <= n; i++) {
        arr[i] = arr[i - 2] + arr[i - 1];
        await delay(500);
        setResult(arr.slice(0, i + 1));
      }
