import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";
import { queue } from "../components/queue-page/Queue";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  array?: string[];
  tail?: number;
  head?: number;
  currentIndex?: number;
  emptyElement?: string;
  emptyArray?: string[];
  setIsLoaderEnqueue?: Dispatcher<boolean>;
  setIsLoaderDequeue?: Dispatcher<boolean>;
  setIsLoaderClear?: Dispatcher<boolean>;
  setIsDisabled?: Dispatcher<boolean>;
  setIsDisabledClear?: Dispatcher<boolean>;
  setValue?: Dispatcher<string>;
  setArray?: Dispatcher<string[]>;
  setTail?: Dispatcher<number>;
  setHead?: Dispatch<number>;
  setCurrentIndex?: Dispatcher<number>;
}

export const enqueue = async ({
  value,
  array,
  currentIndex,
  setArray,
  setIsDisabled,
  setIsDisabledClear,
  setIsLoaderEnqueue,
  setTail,
  setHead,
  setCurrentIndex,
  setValue,
}: IProps) => {
  setIsLoaderEnqueue!(true);
  setIsDisabled!(true);
  const newEl = { number: Number(value), state: ElementStates.Default };

  queue.enqueue(currentIndex!, newEl!, array!);
  setArray!(queue.queue);
  await delay(500);
  setHead!(queue.head);
  setTail!(queue.tail);
  setCurrentIndex!(queue.currentIndex);
  setValue!("");
  setIsDisabled!(false);
  setIsDisabledClear!(false);
  setIsLoaderEnqueue!(false);
};

export const dequeue = async ({
  array,
  head,
  setIsDisabled,
  setIsDisabledClear,
  setIsLoaderDequeue,
  setHead,
  setTail,
  setCurrentIndex,
  setArray,
  emptyElement,
}: IProps) => {
  setIsLoaderDequeue!(true);
  setIsDisabled!(true);
  queue.dequeue(head!, emptyElement!, array!);
  setArray!(queue.queue);
  await delay(500);
  setHead!(queue.head);
  setTail!(queue.tail);
  setCurrentIndex!(queue.currentIndex);
  setIsDisabled!(false);
  setIsLoaderDequeue!(false);
  if (queue.tail === -1 && queue.head === -1) {
    setIsDisabledClear!(true);
  }
};

export const clearQueue = async ({
  setIsDisabled,
  setIsDisabledClear,
  setIsLoaderClear,
  setArray,
  setTail,
  setCurrentIndex,
  setHead,
  emptyArray,
}: IProps) => {
  setIsLoaderClear!(true);
  setIsDisabled!(true);
  queue.clear();
  await delay(500);
  setArray!(emptyArray!);
  setTail!(queue.tail);
  setCurrentIndex!(queue.currentIndex);
  setHead!(queue.head);
  setIsDisabled!(false);
  setIsLoaderClear!(false);
  setIsDisabledClear!(true);
};
