import React, { useState, ChangeEvent, useRef, useEffect } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./queue.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { ElementStates } from "../../types/element-states";

export const QueuePage: React.FC = () => {
  const [value, setValue] = useState("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [array, setArray] = useState([]);
  const [tail, setTail] = useState()

  useEffect(() => {
    const emptyArr = Array.apply(null, Array(7)).map(function () {});
    setArray(emptyArr);
  }, []);

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  const onAdd = () => {
    value ? add(value) : alert("Введите значение в инпут");
  };

  const onDelete = () => {
    array.length ? dequeue() : alert("стек пуст");
  };

  const onClear = () => {
    // stack.length ? clearStack() : alert("стек пуст");
  };

  // const enqueue = (array) => {
  //   for (let i = 0; i < array.length; i++) {
  //     array.push(item);
  //   }
  // };

  // const dequeue = () => {
  //   array.shift()
  // }

  const add = (item: string) => {
    setIsLoader(true);
    array.push(item);
    setArray(array);
    setCurrentIndex(currentIndex + 1);
    setValue("");
    setIsLoader(false);
  };

  const dequeue = () => {
    setIsLoader(true);
    array.shift()
    // stack.pop();
    // setStack([...stack]);
    setCurrentIndex(currentIndex - 1);
    setIsLoader(false);
  };

  const clearStack = () => {
    // setIsLoader(true);
    // while (stack.length > 0) {
    //   stack.pop();
    // }
    // setStack([...stack]);
    // setIsLoader(false);
  };

  console.log(array);

  return (
    <SolutionLayout title="Очередь">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={onAdd}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={onDelete}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={onClear} />
        </div>
        <div>
          <div className={styles.result}>
            {array.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  currentIndex === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={currentIndex === index ? "top" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
