import React, { useState, ChangeEvent } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { delay } from "../../utils/utils";
import { circleColor, arrReverse } from "../../utils/string-utils";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<nstring[]>(null);
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  const reverse = async (value: string) => {
    setIsLoader(true);
    const newArr = value.split("");
    setArr(newArr);
    await delay(1000);
    arrReverse({ arr, setCurrentIndex, setArr, setIsLoader });
    setIsOpen(true);
  };

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input
            onChange={onChange}
            type="text"
            maxLength={11}
            isLimitText={true}
            data-test="input-tag"
          />
          <Button
            text="Развернуть"
            onClick={() => reverse(value)}
            isLoader={isLoader}
            disabled={value ? false : true}
            data-test="button-tag"
          />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item, index) => (
                <Circle
                  letter={item}
                  key={index}
                  extraClass={styles.item}
                  state={circleColor(currentIndex, index, arr)}
                  data-test="circle-tag"
                />
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
