import React, { useState, ChangeEvent, useRef, useEffect } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./queue.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { ElementStates } from "../../types/element-states";

export const QueuePage: React.FC = () => {
  const [value, setValue] = useState<string>("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [queue, setQueue] = useState<string[]>([]);
  const [tail, setTail] = useState(-1);
  const [head, setHead] = useState(0);
  const emptyElement = "";
  const emptyArr: string[] = Array.apply(null, Array(7)).map(
    (el) => emptyElement
  );

  const maxLength = 7;

  useEffect(() => {
    setQueue(emptyArr);
  }, []);

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  const enqueue = (item: string): void => {
    if (value) {
      setIsLoader(true);
      queue.splice(currentIndex, 1, item);
      if (queue.length <= maxLength) {
        setTail(tail + 1);
        setCurrentIndex(currentIndex + 1);
      } else {
        setTail(0);
        setCurrentIndex(0);
      }
      setValue("");
      setIsLoader(false);
    } else {
      alert("введите значение в инпут");
    }
  };

  const dequeue = () => {
    setIsLoader(true);
    queue.splice(head, 1, emptyElement);
    if (queue.length <= maxLength) {
      setHead(head + 1);
    } else {
      setHead(0);
    }
    setIsLoader(false);
  };

  const clearQueue = () => {
    setIsLoader(true);
    setQueue(emptyArr);
    setTail(0);
    setCurrentIndex(0);
    setHead(0);
    setIsLoader(false);
  };

  return (
    <SolutionLayout title="Очередь">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={() => enqueue(value)}
            disabled={queue.length >= maxLength}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={dequeue}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={clearQueue} />
        </div>
        <div>
          <div className={styles.result}>
            {queue.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  tail === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={head === index ? "head" : null}
                tail={tail === index ? "tail" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
