import React, { useState, ChangeEvent, useEffect } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./queue.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { ElementStates } from "../../types/element-states";
import { enqueue, dequeue, clearQueue } from "../../utils/queue-utils";

export const QueuePage: React.FC = () => {
  const [value, setValue] = useState<string>("");
  const [isLoaderEnqueue, setIsLoaderEnqueue] = useState<boolean>(false);
  const [isLoaderDequeue, setIsLoaderDequeue] = useState<boolean>(false);
  const [isLoaderClear, setIsLoaderClear] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [tail, setTail] = useState(-1);
  const [head, setHead] = useState(0);
  const emptyElement = "";
  const emptyArray: string[] = Array.apply(null, Array(7)).map(
    () => emptyElement
  );
  const maxLength = 7;
  const [array, setArray] = useState<string[]>(emptyArray);

  const enqueProps = {
    value,
    array,
    tail,
    currentIndex,
    setArray,
    setIsLoaderEnqueue,
    setTail,
    setCurrentIndex,
    setValue,
    maxLength,
  };

  const dequeueProps = {
    array,
    head,
    setIsLoaderDequeue,
    setArray,
    setHead,
    emptyElement,
  };

  const clearQueueProps = {
    setIsLoaderClear,
    setArray,
    setTail,
    setCurrentIndex,
    setHead,
    emptyArray,
  };

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  return (
    <SolutionLayout title="Очередь">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoaderEnqueue}
            extraClass="mr-5"
            onClick={() => enqueue(enqueProps)}
            disabled={tail === 7 ? true : false}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={() => dequeue(dequeueProps)}
            disabled={head > tail ? true : false}
          />
          <Button
            text="Очистить"
            isLoader={isLoader}
            onClick={() => clearQueue(clearQueueProps)}
          />
        </div>
        <div>
          <div className={styles.result}>
            {array.map((value, index) => (
              <Circle
                letter={value}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  tail === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={head === index ? "head" : null}
                tail={tail === index ? "tail" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
