import {
  Dispatch,
  SetStateAction,
} from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  array: any[];
  item: string;
  setHead?: Dispatcher<number | null>;
  setIsLoader?: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<boolean>;
  setTail?: Dispatcher<number>;
}

export const addHead = ({
  array,
  item,
  setIsLoader,
  setHead,
  setIsChanging,
  setTail,
}: IProps) => {
  if (item) {
    setIsLoader!(true);
    setHead!(-1);
    setIsChanging!(true);
    array.unshift(item);
    setIsChanging!(false);
    setHead!(0);
    setTail!(array.length - 1);
