      setCurrentIndex(currentIndex + 1);
      if (queue.len)
      setTail(tail + 1);
      setValue("");
      setIsLoader(false);
    } else {
      alert("введите значение в инпут");
    }
    };

  const dequeue = () => {
    setIsLoader(true);
    queue.splice(head, 1, emptyElement);
    setHead(head + 1);
    setIsLoader(false);
  };

  // const add = (item: string) => {
