  //   // setStack([...stack]);
  //   setCurrentIndex(currentIndex - 1);
  //   setIsLoader(false);
  // };

  const clearStack = () => {
    // setIsLoader(true);
    // while (stack.length > 0) {
    //   stack.pop();
    // }
    // setStack([...stack]);
    // setIsLoader(false);
  };

  console.log(queue);

  return (
    <SolutionLayout title="Очередь">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={() => enqueue(value)}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={dequeue}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={onClear} />
        </div>
        <div>
          <div className={styles.result}>
            {queue.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  tail === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={head === index ? "head" : null}
                tail={tail === index ? "tail" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
