import React, { useState, ChangeEvent, useEffect } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./list.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { ElementStates } from "../../types/element-states";
import { IArrEl, randomArr } from "../../utils/utils";
import { ArrowIcon } from "../ui/icons/arrow-icon";
import {
  addHead,
  addTail,
  removeHead,
  removeTail,
  addIndex,
  removeIndex,
} from "../../utils/list-utils";

export const ListPage: React.FC = () => {
  const [value, setValue] = useState<string>("");
  const [index, setIndex] = useState<string>("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [array, setArray] = useState<IArrEl[]>([]);
  const [tail, setTail] = useState(-1);
  const [head, setHead] = useState<number>(0);
  const [isChanging, setIsChanging] = useState(false);

  useEffect(() => {
    setArray(randomArr({setArray}));
    setTail(list.length - 1);
  }, [isChanging]);


  return (
    <SolutionLayout title="Связный список">
      <div className={styles.container}>
        <div className={styles.inputs}>
          <div className={styles.input}>
            <Input
              onChange={(e) => setValue(e.currentTarget.value)}
              value={value}
              maxLength={4}
              placeholder="Введите значение"
            />
            <Button
              text="Добавить в head"
              isLoader={isLoader}
              extraClass="mr-5"
              onClick={() =>
                addHead({
                  list,
                  value,
                  setIsLoader,
                  setHead,
                  setIsChanging,
                  setTail,
                })
              }
              disabled={list.length > 6 ? true : false}
            />
            <Button
              text="Добавить в tail"
              isLoader={isLoader}
              extraClass="mr-5"
              onClick={() => addTail({ array, value, setIsLoader, setTail })}
              disabled={list.length > 6 ? true : false}
            />
            <Button
              text="Удалить из head"
              isLoader={isLoader}
              extraClass="mr-5"
              onClick={() =>
                removeHead({ array, setIsLoader, setHead, setTail })
              }
            />
            <Button
              text="Удалить из tail"
              isLoader={isLoader}
              onClick={() => removeTail({ array, setIsLoader, setTail })}
            />
          </div>
          <div className={styles.input}>
            <Input
              onChange={(e) => setIndex(e.currentTarget.value)}
              value={index}
              maxLength={4}
              placeholder="Введите индекс"
            />
            <Button
              text="Добавить по индексу"
              isLoader={isLoader}
              extraClass="mr-5"
              onClick={() =>
                addIndex({ array, value, index, setIsLoader, setHead, setTail })
              }
              disabled={array.length > 6 ? true : false}
            />

            <Button
              text="Удалить по индексу"
              isLoader={isLoader}
              extraClass="mr-20"
              onClick={() =>
                removeIndex({ array, index, setIsLoader, setHead, setTail })
              }
            />
          </div>
        </div>
        <div>
          <div className={styles.result}>
            {array.map((item, index) => (
              <div className={styles.resultCell} key={index}>
                <Circle
                  letter={item.number}
                  key={index}
                  index={index}
                  extraClass={styles.item}
                  state={
                    tail === index
                      ? ElementStates.Changing
                      : ElementStates.Default
                  }
                  head={
                    isChanging ? (
                      <Circle isSmall={true} />
                    ) : head === index ? (
                      "head"
                    ) : null
                  }
                  tail={tail === index ? "tail" : null}
                />
                {index === list.length - 1 ? null : <ArrowIcon />}
              </div>
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
