import { Dispatch, SetStateAction, FunctionComponent } from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  array: any[], loader, head, changing, tail
  item: string;
  setHead: Dispatcher<number[] | null>;
  setIsLoader: Dispatcher<boolean>;
  setIsChanging: Dispatcher<string>;
  setTail: number
}

export const addHead = ( array, item: string, loader, head, changing, tail) => {
    if (item) {
      setIsLoader(true);
      head(-1);
      changing(true);
      array.unshift(item);
      changing(false);
      head(0);
      tail(array.length - 1);
    } else {
      alert("введите значение в инпут");
    }
    loader(false);
  };
