import { Dispatch, SetStateAction } from "react";
impo

export const delay = (ms: number) => {
  return new Promise((resolve) => setTimeout(resolve, ms));
};

export const swap = (arr: any[], a: number, b: number): void => {
  const temp = arr[a];
  arr[a] = arr[b];
  arr[b] = temp;
};

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  setArray: Dispatcher<number[]>;
}

//random array
export const randomArr = ({ setArray }: IProps) => {
  const array = [];
  const max = 100;
  const count: number = getRandomInt(3, 17);
  while (array.length < count) {
    const r: number = Math.floor(Math.random() * max);
    if (array.indexOf(r) === -1) {
      array.push(r);
    }
  }
  setArray(array);
  return array;
};

const getRandomInt = (min: number, max: number): number => {
  return Math.floor(Math.random() * (max - min)) + min;
};


