interface IQueue {
    enqueue: (index: number, item: string, array: string[]) => void;
    dequeue: () => void;
    peak: () => number | null;
    getSize: () => number;
    printStack: () => number[];
    clear: () => void;
    isFull: () => boolean;
  }

  class Queue implements IQueue {

    queue: string[] = [];
     head = 0;
     tail = -1;
     currentIndex = 0;
    private maxSize = 8;

    enqueue = (index: number, item: string, array: string[]) => {
      if (this.getSize() < this.maxSize) {
        array.splice(index, 1, item);
        this.tail++
        this.currentIndex++
        this.queue = array
      } else {
          throw new Error('Слишком много элементов')
      }
    };

    dequeue = (a): void => {
      if (this.getSize()) {
        array.splice(index, 1, item);
      }
    };

    peak = (): T | null => {
      if (this.getSize()) {
        return this.queue[this.getSize() - 1];
      }
      return null;
    };

    clear = (): void => {
      this.queue = [];
    };

    getSize = () => this.queue.length;

    printStack = () => this.queue;

    isFull = () => {
      if (this.getSize() < this.maxSize) {
        return false;
      }
      return true;
    };
  }

  export const queue = new Queue<string>();
