import React, { useState, ChangeEvent } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./stack.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { ElementStates } from "../../types/element-states";
import { pushStack, popStack, clearStack } from "../../utils/stack-utils";

export const StackPage: React.FC = () => {
  const [value, setValue] = useState("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [array, setArray] = useState<string[]>([]);
  const pushProps = {
    value,
    setIsLoader,
    setArray,
    setCurrentIndex,
    currentIndex,
    setValue,
  };
  const popProps = {
    setIsLoader,
    setArray,
    setCurrentIndex,
    currentIndex,
  };
  const clearProps = {
    setIsLoader,
    setArray,
  };

  return (
    <SolutionLayout title="Стек">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={(e)=>setValue(e.currentRarget.value)} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={()=>pushStack(pushProps)}
            disabled={value ? false : true}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={()=>popStack(popProps)}
            disabled={array.length ? false : true}
          />
          <Button
            text="Очистить"
            isLoader={isLoader}
            onClick={()=>clearStack(clearProps)}
            disabled={array.length ? false : true}
          />
        </div>
        <div>
          <div className={styles.result}>
            {array?.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  currentIndex === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={currentIndex === index ? "top" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
