import React, { useState, useEffect } from "react";
import { Button } from "../ui/button/button";
import { RadioInput } from "../ui/radio-input/radio-input";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./sorting.module.css";
import { bubbleSort, selectionSort } from "../../utils/sorting-utils";
import { UP, DOWN } from "../../constants/directions";
import { Column } from "../ui/column/column";

export const SortingPage: React.FC = (): JSX.Element => {
  const [method, setMethod] = useState<string | undefined>(undefined);
  const [randomArray, setRandomArray] = useState<number[]>([]);
  expoconst [result, setResult] = useState<number[]>([]);

  //random count
  const getRandomInt = (min: number, max: number): number => {
    return Math.floor(Math.random() * (max - min)) + min;
  };

  //random array
  const randomArr = () => {
    const array = [];
    const max = 100;
    const count: number = getRandomInt(3, 17);
    while (array.length < count) {
      const r: number = Math.floor(Math.random() * max);
      if (array.indexOf(r) === -1) {
        array.push(r);
      }
    }
    setRandomArray(array);
    return array
  };

  useEffect(() => {
    setRandomArray(randomArr());
  }, []);


  const selection = (array: number[], direction: string) => {
    selectionSort(array, direction);
    setResult(array);
  };

  const bubble = (array: number[], direction: string) => {
    bubbleSort(array, direction);
    setResult(array);
  };


  return (
    <SolutionLayout title="Сортировка массива">
      <div className={styles.container}>
        <div className={styles.navigation}>
          <div className={styles.navigation}>
            <RadioInput
              label="Выбор"
              defaultChecked
              extraClass={styles.navigationItem}
              onClick={() => setMethod("selection")}
            />
            <RadioInput
              label="Пузырёк"
              extraClass={styles.navigationItem}
              onClick={() => setMethod("bubble")}
            />
            <Button
              text="По возрастанию"
              extraClass={styles.navigationItem}
              onClick={
                method === "selection"
                  ? () => selection(randomArray, UP)
                  : () => bubble(randomArray, UP)
              }
            />
            <Button
              text="По убыванию"
              extraClass={styles.navigationItem}
              onClick={
                method === "selection"
                  ? () => selection(randomArray, DOWN)
                  : () => bubble(randomArray, DOWN)
              }
            />
          </div>
          <Button text="Новый массив" onClick={() => randomArr()} />
        </div>
        {result?.length ? (
          <div className={styles.result}>
            {result.map((el, index) => (
              <div key={index} className={styles.resultItem}>
                <Column index={el}/>
              </div>
            ))}
          </div>
        ) : null}
      </div>
    </SolutionLayout>
  );
};
