import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";
import { stack } from "../components/stack-page/stack_class";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  array: string[];
  currentIndex?: number;
  setIsLoader: Dispatcher<boolean>;
  setValue?: Dispatcher<string>;
  setArray: Dispatcher<string[]>;
  setCurrentIndex?: Dispatcher<number>;
}

export const pushStack = ({
  value,
  setIsLoader,
  setArray,
  setCurrentIndex,
  currentIndex,
  setValue,
}: IProps): void => {
  setIsLoader(true);
  stack.push(value!);
  setArray(stack.printStack());
  setCurrentIndex!(currentIndex! + 1);
  setValue!("");
  setIsLoader(false);
};

export const popStack = ({
  array,
  setIsLoader,
  setArray,
  setCurrentIndex,
  currentIndex,
}: IProps): void => {
  setIsLoader(true);
  stack.pop();
  setArray(stack.printStack());
  setCurrentIndex!(currentIndex! - 1);
  setIsLoader(false);
};

export const clearStack = ({ array, setIsLoader, setArray }: IProps): void => {
  setIsLoader(true);
  while (array.length > 0) {
    stack.pop();
  }
  setArray([...array]);
  setIsLoader(false);
};
