import { Dispatch, SetStateAction } from "react";
import { list } from "../components/list-page/List";
import { IArrEl } from "../utils/utils";
import { ElementStates } from "../types/element-states";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps<T> {
  array?: T[];
  value?: number;
  index?: number;
  setArray?: Dispatcher<T[]>;
  setHead?: Dispatcher<number>;
  setIsLoader?: Dispatcher<boolean>;
  setIsDisabled?: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<boolean>;
  setTail?: Dispatcher<number>;
}

// добавить в head
export const addHead = ({
  array,
  value,
  setIsLoader,
  setIsDisabled,
  setHead,
  setIsChanging,
  setTail,
  setArray,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  setIsChanging!(true);
  const newEl = { number: value, state: ElementStates.Default };
  list.unshift(newEl, array!);
  setArray!(list.print());
  setIsChanging!(false);
  setHead!(list.head);
  setTail!(list.tail);
  setIsLoader!(false);
  setIsDisabled!(false);
};

export const addTail = ({
  array,
  value,
  setIsLoader,
  setArray,
  setHead,
  setIsChanging,
  setTail,
  setIsDisabled,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  const newEl = { number: value, state: ElementStates.Default };
  list.push(newEl, array!);
  setArray!(list.print());
  setIsChanging!(false);
  setHead!(list.head);
  setTail!(list.tail);
  setIsLoader!(false);
  setIsDisabled!(false);
};

export const removeHead = ({
  array,
  setIsLoader,
  setArray,
  setIsDisabled,
  setHead,
  setTail,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  list.shift(array!);
  setArray!(list.print());
  setHead!(list.head);
  setTail!(list.tail);
  setIsDisabled!(false);
  setIsLoader!(false);
};

export const removeTail = ({
  array,
  setIsLoader,
  setArray,
  setIsDisabled,
  setHead,
  setTail,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  list.pop(array!);
  setArray!(list.print());
  setHead!(list.head);
  setTail!(list.tail);
  setIsDisabled!(false);
  setIsLoader!(false);
};

export const addIndex = ({
  array,
  value,
  index,
  setIsLoader,
  setIsDisabled,
  setHead,
  setTail,
  setArray,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  const newEl = { number: value, state: ElementStates.Default };
  list.addInd(index!, newEl!, array!);
  setArray!(list.print());
  setHead!(list.head);
  setTail!(list.tail);
  setIsDisabled!(false);
  setIsLoader!(false);
};

export const removeIndex = ({
  array,
  index,
  setIsLoader,
  setIsDisabled,
  setHead,
  setTail,
  setArray,
}: IProps<IArrEl>) => {
  setIsLoader!(true);
  setIsDisabled!(true);
  list.removeInd(index!, array!);
  setArray!(list.print());
  setHead!(list.head);
  setTail!(list.tail);
  setIsDisabled!(false);
  setIsLoader!(false);
};
