export const circleColor = (
  index: number,
  currentIndex: number,
  arr: Array<string | number>
) => {
  let length = arr.length - 1;
  if (currentIndex < index || currentIndex > length - index) {
    return elementSstates.MODIFIED;
  }
  if (currentIndex === index || currentIndex === length - index) {
    return elementSstates.CHANGING;
  }
  return elementSstates.DEFAULT;
};
