type Props = {
  value: string,
      isLoader: boolean,
      currentIndex: number,
      stack: string[]
}

class StackPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      isLoader: false,
      currentIndex: -1,
      stack: []
    };
  }

  onChange = (e: ChangeEvent<HTMLInputElement>) => {
    this.setState({ value: e.target.value });
  };

  onAdd = () => {
    const { value } = this.state;
    value ? this.pushStack() : alert("Введите значение в инпут");
  };

  onDelete = () => {
    const { stack } = this.state;
    stack.length ? this.popStack() : alert("стек пуст");
  };

  onClear = () => {
    const { stack } = this.state;
    stack.length ? this.clearStack() : alert("стек пуст");
  };

  render() {
    const { value, isLoader, currentIndex, stack } = this.state;

    return (
      <SolutionLayout title="Стек">
        <div className={styles.container}>
          <div className={styles.input}>
            <Input onChange={this.onChange} value={value} maxLength={4} />
            <Button
              text="Добавить"
              isLoader={isLoader}
              extraClass="mr-5"
              onClick={this.onAdd}
            />
            <Button
              text="Удалить"
              isLoader={isLoader}
              extraClass="mr-20"
              onClick={this.onDelete}
            />
            <Button text="Очистить" isLoader={isLoader} onClick={this.onClear} />
          </div>
          <div>
            <div className={styles.result}>
              {stack?.map((item, index) => (
                <Circle
                  letter={item}
                  key={index}
                  index={index}
                  extraClass={styles.item}
                  state={
                    currentIndex === index
                      ? ElementStates.Changing
                      : ElementStates.Default
                  }
                  head={currentIndex === index ? "top" : null}
                />
              ))}
            </div>
          </div>
        </div>
      </SolutionLayout>
    );
  }
}

export default StackPage;
