import React, { useState } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { ArrowUpIcon } from "@ya.praktikum/react-developer-burger-ui-components";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<null | any[]>(null);

  const expand = (value: string) => {
    const newArr = Array.from(value)
    // setArr(Array.from(value));
    quickSortRecursive(newArr, 0, newArr.length-1)
    setArr(newArr);
console.log(newArr)
    setIsOpen(true);
  };

  const delay = (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const onChange = (e: any) => {
    setValue(e.target.value);
  };

  const swap = (arr: any[], a: number, b: number): void => {
    const temp = arr[a];
    arr[a] = arr[b];
    arr[b] = temp;
  };

  const  partition = (arr, start, end) => {
    // Taking the last element as the pivot
    const pivotValue = arr[end];
    let pivotIndex = start; 
    for (let i = start; i < end; i++) {
        if (arr[i] < pivotValue) {
        // Swapping elements
        [arr[i], arr[pivotIndex]] = [arr[pivotIndex], arr[i]];
        // Moving to next element
        pivotIndex++;
        }
    }

    // Putting the pivot value in the middle
    [arr[pivotIndex], arr[end]] = [arr[end], arr[pivotIndex]]
    return pivotIndex;

};
const quickSortRecursive = async(arr, start, end) => {
  // Base case or terminating case
  if (start >= end) {
      return;
  }

  // Returns pivotIndex
  let index = partition(arr, start, end);

  // Recursively apply the same logic to the left and right subarrays
  quickSortRecursive(arr, start, index - 1);
  quickSortRecursive(arr, index + 1, end);

}


  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} />
          <Button text="Развернуть" onClick={() => expand(value)} />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item: string) => (
                <Circle letter={item} />
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
