import { ElementStates } from "../types/element-states";
import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  arr?: string[];
  new
  setReverse: Dispatcher<number[] | null>;
  setIsLoader: Dispatcher<boolean>;
  setArr: Dispatcher<string[]>;
  setIsOpen: Dispatch<boolean>;
  setCurrentIndex: Dispatch<number>;
}

export const circleColor = (
  index: number,
  currentIndex: number,
  arr: Array<string | number>
) => {
  let length = arr.length - 1;
  if (currentIndex < index || currentIndex > length - index) {
    return ElementStates.Modified;
  }
  if (currentIndex === index || currentIndex === length - index) {
    return ElementStates.Changing;
  }
  return ElementStates.Default;
};

export const reverse = ({
  value,
  setIsLoader,
  setArr,
  setIsOpen,
}: IProps): void => {
  setIsLoader(true);
  const newArr = value!.split("");
  setArr(newArr);
  arrReverse(newArr);
  setIsOpen(true);
};

const arrReverse = async ({newArr, setCurrentIndex, setArr, setIsLoader}: IProps) => {
  const end = arr.length - 1;
  const mid = Math.floor(arr.length / 2);
  setCurrentIndex(0);
  for (let i = 0; i < mid; i++) {
    [arr[i], arr[end - i]] = [arr[end - i], arr[i]];
    setCurrentIndex((i) => i + 1);
    await delay(1000);
    setArr(arr);
  }
  setIsLoader(false);
};
