import { Dispatch, SetStateAction } from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  list?: string[];
  item?: string;
  index?: number;
  setHead?: Dispatcher<number | null>;
  setIsLoader?: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<boolean>;
  setTail?: Dispatcher<number>;
}

export const addHead = ({
  list,
  item,
  setIsLoader,
  setHead,
  setIsChanging,
  setTail,
}: IProps) => {
  if (item) {
    setIsLoader!(true);
    setHead!(-1);
    setIsChanging!(true);
    list!.unshift(item);
    setIsChanging!(false);
    setHead!(0);
    setTail!(list!.length - 1);
  } else {
    alert("введите значение в инпут");
  }
  setIsLoader!(false);
};

export const addTail = ({ list, item, setIsLoader, setTail }: IProps) => {
  if (item) {
    setIsLoader!(true);
    list!.push(item);
    setTail!(list!.length - 1);
  } else {
    alert("введите значение в инпут");
  }
  setIsLoader!(false);
};

export const removeHead = ({ list, setIsLoader, setHead }: IProps) => {
  setIsLoader!(true);
  list!.shift();
  setHead!(0);
  setIsLoader!(false);
};

