import React, { useState, ChangeEvent } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./stack.module.css";
import { Button } from "../ui/button/button";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { circleColor } from "../../utils/stack-utils";

export const StackPage: React.FC = () => {
  const [value, setValue] = useState("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [stack, setStack] = useState<string[]>([]);
  const [isChanging, setIsChanging] = useState(false);

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  const onAdd = () => {
    value ? pushStack(value) : alert("Введите значение в инпут");
  };

  const onDelete = () => {
    stack.length ? popStack() : alert("стек пуст");
  };

  const onClear = () => {
    stack.length ? clearStack() : alert("стек пуст");
  };

  const pushStack = (item: string) => {
    setIsLoader(true);
    stack.push(item);
    setStack([...stack]);
    setCurrentIndex(currentIndex + 1);
    setValue("");
    setIsLoader(false);
  };

  console.log(currentIndex);

  const popStack = () => {
    setIsLoader(true);
    stack.pop();
    setStack([...stack]);
    setCurrentIndex(currentIndex - 1);
    setIsLoader(false);
  };

  const clearStack = () => {
    setIsLoader(true);
    while (stack.length > 0) {
      stack.pop();
    }
    setStack([...stack]);
    setIsLoader(false);
  };

  return (
    <SolutionLayout title="Стек">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={onAdd}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={onDelete}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={onClear} />
        </div>
        <div>
          <div className={styles.result}>
            {stack?.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                isChanging={isChanging}
                state={circleColor(isChanging)}
                head={currentIndex === index + 1 ? "top" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
