import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";
import {queue} from '../components/queue-page/queue_class'

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  array?: string[];
  tail?: number;
  head?: number;
  currentIndex?: number;
  emptyElement?: string;
  emptyArray?: string[];
  setIsLoaderEnqueue?: Dispatcher<boolean>;
  setIsLoaderDequeue?: Dispatcher<boolean>;
  setIsLoaderClear?: Dispatcher<boolean>;
  setIsDisabled?: Dispatcher<boolean>;
  setValue?: Dispatcher<string>;
  setArray?: Dispatcher<string[]>;
  setTail?: Dispatcher<number>;
  setHead?: Dispatch<number>;
  setCurrentIndex?: Dispatcher<number>;
}

export const enqueue = async({
  value,
  array,
  currentIndex,
  setArray,
  setIsDisabled,
  setIsLoaderEnqueue,
  setTail,
  setHead,

  setCurrentIndex,
  setValue,
}: IProps) => {
  setIsLoaderEnqueue!(true);
  setIsDisabled!(true)
    queue.enqueue(currentIndex!, value!, array!);
    setArray!(queue.queue)
        await delay(500)
        setHead!(queue.head)

    setTail!(queue.tail)
    setCurrentIndex!(queue.currentIndex)
    setValue!("");
    setIsDisabled!(false)
    setIsLoaderEnqueue!(false);  
};

export const dequeue = async({
  array,
  head,
  setIsDisabled,
  setIsLoaderDequeue,
  setHead,
  setTail,
  setArray,
  emptyElement,
}: IProps) => {
  setIsLoaderDequeue!(true);
  setIsDisabled!(true)
  queue.dequeue(head!, emptyElement!, array!);
  setArray!(queue.queue)
  await delay(500)

  setHead!(queue.head)
  setTail!(queue.tail)
  setIsDisabled!(false)
  setIsLoaderDequeue!(false);
};

export const clearQueue = async({
  setIsDisabled,
  setIsLoaderClear,
  setArray,
  setTail,
  setCurrentIndex,
  setHead,
  emptyArray
}: IProps) => {
  setIsLoaderClear!(true);
  setIsDisabled!(true)
  queue.clear()
  await delay(500)
  setArray!(emptyArray!)
  setTail!(queue.tail);
  setCurrentIndex!(queue.currentIndex);
  setHead!(queue.head);
  setIsDisabled!(false)
  setIsLoaderClear!(false);
};
