const swap = (arr: number[], a: number, b: number): void => {
  const temp = arr[a];
  arr[a] = arr[b];
  arr[b] = temp;
};

// по возрастанию
export const bubbleSort = (arr: number[], direction: string) => {

  for (let j = arr.length - 1; j > 0; j--) {
    for (let i = 0; i < j; i++) {
      if (arr[i] > arr[i + 1]) {
        swap(arr, i, i + 1);
      }
    }
  }
  console.log(arr);
  return arr;
};

// по убыванию
export const selectionSort = (arr: number[]) => {
  const { length } = arr;
  for (let i = 0; i < length - 1; i++) {
    let maxInd = i;
    for (let j = i; j < length; j++) {
      if (arr[maxInd] < arr[j]) {
        maxInd = j;
      }
    }
    if (i !== maxInd) {
      swap(arr, i, maxInd);
    }
  }
  console.log(arr)
  return arr;
};
