import React, { useState } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { swap, delay } from "../../utils/utils";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<null | string[]>(null);

  const reverse = (value: string) => {
    const newArr = Array.from(value);
    setArr(arrReverse(newArr));
    setIsOpen(true);
  };

  const onChange = (e: any) => {
    setValue(e.target.value);
  };

  const arrReverse = (arr: string[]) => {
    for (let i = 0; i < Math.round(arr.length / 2); i++) {
      swap(arr, i, arr.length - 1 - i);
    }
    return arr;
  };

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} />
          <Button text="Развернуть" onClick={() => reverse(value)} />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item, index) => (
                <Circle letter={item} key={index} extr/>
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
