          <div className={styles.navigation}>
            <RadioInput label="Выбор" extraClass={styles.navigationItem} />
            <RadioInput label="Пузырёк" extraClass={styles.navigationItem} />
            <Button
              text="По возрастанию"
              extraClass={styles.navigationItem}
              onClick={() => bubbleSort(randomArray)}
            />
            <Button text="По убыванию" extraClass={styles.navigationItem} 
