import { Dispatch, SetStateAction } from "react";
import { delay } from "../utils/utils";
import { list } from "../components/list-page/List";
import { IArrEl } from "../utils/utils";
import { ElementStates } from "../types/element-states";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps<T> {
  array?: T[];
  value?: number;
  index?: number;
  setArray: Dispatcher<T[]>;
  setHead: Dispatcher<number>;
  setIsLoader: Dispatcher<boolean>;
  setIsDisabled: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<string>;
  setTail: Dispatcher<number>;
  setCurrentIndex?: Dispatcher<number>;
}

// добавить в head
export const addHead = async ({
  array,
  value,
  setIsLoader,
  setIsDisabled,
  setHead,
  setTail,
  setArray,
  setIsChanging,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  setIsChanging!("head");
  const newEl = { number: value, state: ElementStates.Default };
  newEl.state = ElementStates.Changing;
  await delay(500);
  list.unshift(newEl, array!);
  await delay(500);
  setArray(list.print());
  newEl.state = ElementStates.Modified;
  setHead(list.head);
  setTail(list.tail);
  setIsChanging!("");
  await delay(500);
  newEl.state = ElementStates.Default;
  setIsLoader(false);
  setIsDisabled(false);
};

// добавить в tail
export const addTail = async ({
  array,
  value,
  setIsLoader,
  setArray,
  setHead,
  setTail,
  setIsDisabled,
  setIsChanging,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  setIsChanging!("tail");
  const newEl = { number: value, state: ElementStates.Default };
  newEl.state = ElementStates.Changing;
  await delay(500);
  list.push(newEl, array!);
  await delay(500);
  setArray(list.print());
  newEl.state = ElementStates.Modified;
  setHead(list.head);
  setTail(list.tail);
  setIsChanging!("");
  await delay(500);
  newEl.state = ElementStates.Default;
  setIsLoader(false);
  setIsDisabled(false);
};

// удалить из head
export const removeHead = ({
  array,
  setIsLoader,
  setArray,
  setIsDisabled,
  setHead,
  setTail,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  list.shift(array!);
  setArray(list.print());
  setHead(list.head);
  setTail(list.tail);
  setIsDisabled!(false);
  setIsLoader!(false);
};

// удалить из tail
export const removeTail = ({
  array,
  setIsLoader,
  setArray,
  setIsDisabled,
  setHead,
  setTail,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  list.pop(array!);
  setArray(list.print());
  setHead(list.head);
  setTail(list.tail);
  setIsDisabled(false);
  setIsLoader(false);
};

//добавить по индексу
export const addIndex = async ({
  array,
  value,
  index,
  setIsLoader,
  setIsDisabled,
  setHead,
  setTail,
  setArray,
  setIsChanging,
  setCurrentIndex,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  setIsChanging!("index");
  list.se
  const newEl = { number: value, state: ElementStates.Default };
  newEl.state = ElementStates.Changing;
  list.addInd(index!, newEl!, array!);
  setCurrentIndex!(list.currentIndex);
  await delay(500);

  setArray(list.print());
  newEl.state = ElementStates.Modified;
  await delay(500);
  newEl.state = ElementStates.Default;
  setHead(list.head);
  setTail(list.tail);
  setIsDisabled(false);
  setIsLoader(false);
  setIsChanging!("");
};

//удалить по индексу
export const removeIndex = ({
  array,
  index,
  setIsLoader,
  setIsDisabled,
  setHead,
  setTail,
  setArray,
}: IProps<IArrEl>) => {
  setIsLoader(true);
  setIsDisabled(true);
  list.removeInd(index!, array!);
  setArray(list.print());
  setHead(list.head);
  setTail(list.tail);
  setIsDisabled(false);
  setIsLoader(false);
};
