import { delay } from "./utils";
import { Dispatch, SetStateAction, FunctionComponent } from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
    n: number,
    setResult?: Dispatcher<number>,
    setIsLoader?: Dispatcher<boolean>,
    setValue?: Dispatcher<string>
}

export const fibonacci: FunctionComponent<IProps> = async (props: IProps) => {
    if (n >= 1 && n <= 19) {
      let arr = [0, 1];
      for (let i = 2; i <= n; i++) {
        arr[i] = arr[i - 2] + arr[i - 1];
        await delay(500);
        setResult(arr.slice(0, i + 1));
      }
      setIsLoader(false)
      setValue('')
    } else {
      setIsLoader(false)
      return null;
    }
  };
