import {
  Dispatch,
  SetStateAction,
} from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  array: any[];
  item: string;
  setHead?: Dispatcher<number | null>;
  setIsLoader?: Dispatcher<boolean>;
  setIsChanging?: Dispatcher<boolean>;
  setTail?: Dispatcher<number>;
}

export const addHead = ({
  array,
  item,
  setIsLoader,
  setHead,
  setIsChanging,
  setTail,
}: IProps) => {
  if (item) {
    setIsLoader!(true);
    setHead!(-1);
    setIsChanging!(true);
    array.unshift(item);
    setIsChanging!(false);
    setHead!(0);
    setTail!(array.length - 1);
  } else {
    alert("введите значение в инпут");
  }
  setIsLoader!(false);
};

export const addTail = ({array, item, setIsLoader, setTail} : IProps) => {
  if (item) {
    setIsLoader!(true);
    array.push(item);
    setTail!(array.length - 1);
  } else {
    alert("введите значение в инпут");
  }
  setIsLoader!(false);
};

export const removeHead = ({array, setIsLoader, setHead} : IProps) => {
  setIsLoader!(true);
  array.shift();
  setHead!(0);
  setIsLoader!(false);
};

