  //     if (queue.length <= maxLength) {
  //       setTail(tail + 1);
  //       setCurrentIndex(currentIndex + 1);
  //     } else {
  //       setTail(0);
  //       setCurrentIndex(0);
  //     }
  //     setValue("");
  //     setIsLoader(false);
  //   } else {
  //     alert("введите значение в инпут");
  //   }
  // };

  // const dequeue = () => {
  //   setIsLoader(true);
  //   queue.splice(head, 1, emptyElement);
  //   if (queue.length <= maxLength) {
  //     setHead(head + 1);
  //   } else {
