import React, { useState } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { ArrowUpIcon } from "@ya.praktikum/react-developer-burger-ui-components";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<null | any[]>(null);

  const expand = (value: string) => {
    const newArr = Array.from(value)
    setArr(Array.from(value));
    quickSort(newArr, 0, newArr.length-1)
    // reverseString(value);
    setIsOpen(true);
  };

  const delay = (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  // const reverseString = async (arr: string) => {
  //   let reversedString = "";

  //   for (let i = arr.length; i > 0; i--) {
  //     reversedString += arr.substring(i, i - 1);
  //   }
  //   await delay(1000);
  //   const reversedArr = Array.from(reversedString);
  //   setArr(reversedArr);
  // };

  const onChange = (e: any) => {
    setValue(e.target.value);
  };

  const swap = (arr: any[], a: number, b: number): void => {
    const temp = arr[a];
    arr[a] = arr[b];
    arr[b] = temp;
  };

  const partition = (
    arr: any[],
    start = 0,
    end = arr.length - 1
  ): number => {
    if (arr.length <= 1) {
      return 0;
    } else {
      const pivot = arr[Math.floor((start + end) / 2)];
      let l = start;
      let r = end;

      while (l <= r) {
        while (arr[l] < pivot) {
          l++;
        }
        while (arr[r] > pivot) {
          r--;
        }

        if (l <= r) {
          swap(arr, start, end);
          l++;
          r--;
        }
      }
      return l;
    }
  };

  const quickSort = async (arr: any[], start: number, end: number) => {
    let index = 0;
    if (arr.length > 1) {
      index = partition(arr, start, end);

      if (start < index - 1) {
        quickSort(arr, start, index - 1);
      }
      if (index < end) {
        quickSort(arr, index, end);
      }
    }

    console.log(index)
    return arr;
  };
  // берем первый и последний элемент
  // меняем местами
  // i - 1 и start+1
  //меняем местами и тд

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} />
          <Button text="Развернуть" onClick={() => expand(value)} />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item: string) => (
                <Circle letter={item} />
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
