import React, { useState, ChangeEvent } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { swap, delay } from "../../utils/utils";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<null | string[]>(null);
  const [isLoader, setIsLoader] = useState<boolean>(false);


  const reverse = (value: string) => {
    setIsLoader(true);
    const newArr = Array.from(value);
    setArr(newArr)
    arrReverse(newArr)
    setIsOpen(true);
  };

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  const arrReverse = async(arr: string[]) => {
    let end = arr.length - 1;
    let mid = Math.floor(arr.length/2);
    let start = 0
    for (let i = 0; i < mid; i++) {
      swap(arr, i, end - i);
      await delay(1000);
      setArr(arr.slice(0, i + 1));
    }
    setIsLoader(false)
  };

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} />
          <Button text="Развернуть" onClick={() => reverse(value)} isLoader={isLoader}/>
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item, index) => (
                <Circle letter={item} key={index} extraClass={styles.item}/>
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
