import React from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";

export const ListPage: React.FC = () => {
  return (
    <SolutionLayout title="Связный список">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={() => enqueue(value)}
            disabled={queue.length > maxLength ? true : false}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={dequeue}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={clearQueue} />
        </div>
        <div>
          <div className={styles.result}>
            {queue.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  tail === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={head === index ? "head" : null}
                tail={tail === index ? "tail" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
