import React, { useState } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Circle } from "../ui/circle/circle";
import { Button } from "../ui/button/button";
import styles from "./string.module.css";
import { ArrowUpIcon } from "@ya.praktikum/react-developer-burger-ui-components";

export const StringComponent: React.FC = () => {
  const [value, setValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [arr, setArr] = useState<null | string[]>(null);

  const expand = (value: string) => {
    const newArr = Array.from(value);
    setArr(quickSort(newArr));
    setIsOpen(true);
  };

  const delay = (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const onChange = (e: any) => {
    setValue(e.target.value);
  };

  const swap = (arr: string[], a: number, b: number): void => {
    const temp = arr[a];
    arr[a] = arr[b];
    arr[b] = temp;
  };

  const partition = (arr: string[], left: number, right: number) => {
    const pivot = arr[Math.floor((left + right) / 2)];

    while (left <= right) {
      while (arr[left] < pivot) {
        left++;
      }

      while (arr[right] > pivot) {
        right--;
      }

      if (left <= right) {
        swap(arr, left, right);
        left++;
        right--;
      }
    }
    return left;
  };

  const quickSort = (arr: string[]) => {
    return quickSortHelper(arr, 0, arr.length - 1);
  };

  const quickSortHelper = (arr: string[], left: number, right: number) => {
    if (arr.length < 2) {
      return arr;
    }

    const index = partition(arr, left, right);


    if (arr.indexleft < index - 1) {
      quickSortHelper(arr, left, index - 1);
    }

    if (index < right) {
      quickSortHelper(arr, index, right);
    }

    return arr;
  };

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} />
          <Button text="Развернуть" onClick={() => expand(value)} />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {arr?.map((item, index) => (
                <Circle letter={item} key={index}/>
              ))}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
