import React, { useState } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import styles from "./fibonacci.module.css";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle"
import { delay } from "../../utils/utils";

export const FibonacciPage: React.FC = () => {

  const [value, setValue] = useState<any>();
  const [isOpen, setIsOpen] = useState(false);
  const [result, setResult] = useState<null | any[]>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  const onChange = (e: any) => {
    setValue(e.target.value);
  };

  const onClick = (value: number) => {
    setResult(null)
    setIsLoading(true);
    fibonacci(value);
    setIsOpen(true);
  };

  const fibonacci = async(n: number) => {
    if (n >= 1 && n <= 19) {
      let arr = [0, 1];
      for (let i = 2; i <= n; i++) {
        arr[i] = arr[i - 2] + arr[i - 1];
        await delay(1000);
        setResult(arr.slice(0, i+1));  
      }
    } else {
      return null;
    }
  };


  return (
    <SolutionLayout title="Последовательность Фибоначчи">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} type="number" min="1" max="19" step="1" />
          <Button text="Рассчитать" onClick={() => onClick(value)} />
        </div>
        <div>
          {isOpen ? (
            <div className={styles.result}>
              {result?.length ? (
                result.map((item, index) => (
                  <Circle letter={item} key={index} extraClass={styles.item}/>
                ))
              ) : (
                <p>введено недопустимое значение</p>
              )}
            </div>
          ) : null}
        </div>
      </div>
    </SolutionLayout>
  );
};
