import { delay } from "./utils";
import { Dispatch, SetStateAction } from "react";
import { stack } from "../components/stack-page/stack_class";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  value?: string;
  currentIndex?: number;
  setIsLoaderCle: Dispatcher<boolean>;

  setIsLoaderClear: Dispatcher<boolean>;
  setValue?: Dispatcher<string>;
  setArray: Dispatcher<string[]>;
  setCurrentIndex?: Dispatcher<number>;
}

export const pushStack = async ({
  value,
  setIsLoader,
  setArray,
  setCurrentIndex,
  currentIndex,
  setValue,
}: IProps) => {
  setIsLoader(true);
  stack.push(value!);
  setArray(stack.printStack());
  await delay(500);
  setCurrentIndex!(currentIndex! + 1);
  setValue!("");
  setIsLoader(false);
};

export const popStack = async ({
  setIsLoader,
  setArray,
  setCurrentIndex,
}: IProps) => {
  setIsLoader(true);
  await delay(500);
  stack.pop();
  setArray(stack.printStack());
  setCurrentIndex!(stack.getSize() - 1);
  setIsLoader(false);
};

export const clearStack = async ({ setIsLoader, setArray }: IProps) => {
  setIsLoader(true);
  await delay(500);
  stack.clear();
  setArray(stack.printStack());
  setIsLoader(false);
};
