import React from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";

export const QueuePage: React.FC = () => {
  const [value, setValue] = useState("");
  const [isLoader, setIsLoader] = useState<boolean>(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [stack, setStack] = useState<string[]>([]);

  const onChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  const onAdd = () => {
    value ? pushStack(value) : alert("Введите значение в инпут");
  };

  const onDelete = () => {
    stack.length ? popStack() : alert("стек пуст");
  };

  const onClear = () => {
    stack.length ? clearStack() : alert("стек пуст");
  };

  const pushStack = (item: string) => {
    setIsLoader(true);
    stack.push(item);
    setStack([...stack]);
    setCurrentIndex(currentIndex + 1);
    setValue("");
    setIsLoader(false);
  };

  const popStack = () => {
    setIsLoader(true);
    stack.pop();
    setStack([...stack]);
    setCurrentIndex(currentIndex - 1);
    setIsLoader(false);
  };

  const clearStack = () => {
    setIsLoader(true);
    while (stack.length > 0) {
      stack.pop();
    }
    setStack([...stack]);
    setIsLoader(false);
  };

  return (
    <SolutionLayout title="Очередь">
      <div className={styles.container}>
        <div className={styles.input}>
          <Input onChange={onChange} value={value} maxLength={4} />
          <Button
            text="Добавить"
            isLoader={isLoader}
            extraClass="mr-5"
            onClick={onAdd}
          />
          <Button
            text="Удалить"
            isLoader={isLoader}
            extraClass="mr-20"
            onClick={onDelete}
          />
          <Button text="Очистить" isLoader={isLoader} onClick={onClear} />
        </div>
        <div>
          <div className={styles.result}>
            {queue?.map((item, index) => (
              <Circle
                letter={item}
                key={index}
                index={index}
                extraClass={styles.item}
                state={
                  currentIndex === index
                    ? ElementStates.Changing
                    : ElementStates.Default
                }
                head={currentIndex === index ? "top" : null}
              />
            ))}
          </div>
        </div>
      </div>
    </SolutionLayout>
  );
};
