interface IQueue<T> {
  enqueue: (index: number, item: string, array: string[]) => void;
  dequeue: (index: number, item: string, array: string[]) => void;
  peak: () => number | null;
  getSize: () => number;
  clear: () => void;
  isFull: () => boolean;
}

class Queue<T> implements IQueue<T> {
  queue: string[] = [];
  head = 0;
  tail = -1;
  currentIndex = 0;
  private maxSize = 8;

  enqueue = (index: number, item: string, array: string[]) => {
    if (this.getSize() < this.maxSize) {
      array.splice(index, 1, item);
      this.tail++;
      this.currentIndex++;
      this.queue = array;
    } else {
      throw new Error("Слишком много элементов");
    }
  };

  dequeue = (index: number, item: string, array: string[]): void => {
    if (this.tail > this.head) {
      array.splice(index, 1, item);
      this.head++;
    } else {
      this.head = 0;
    }
    this.queue = array;
  };

  clear = (): void => {
    this.queue = [];
    this.head = 0
    this.tail =0
    this.currentIndex =0
  };

  getSize = () => this.queue.length;

}

export const queue = new Queue<string>();
