import { Dispatch, SetStateAction, FunctionComponent } from "react";

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  array: any[], 
  item: string;
  setHead: Dispatcher<number | null>;
  setIsLoader: Dispatcher<boolean>;
  setIsChanging: Dispatcher<boolean>;
  setTail: Dispatcher<number>
}

export const addHead = ( array, item: string, setIsLoader, head, changing, tail) => {
    if (item) {
      setIsLoader(true);
      setHead(-1);
      setIsChanging(true);
      array.unshift(item);
      setIsChanging(false);
      setHead(0);
      setTail(array.length - 1);
    } else {
      alert("введите значение в инпут");
    }
    setIsLoader(false);
  };
