import { ElementStates } from "../types/element-states";
import { Dispatch, SetStateAction } from "react";
imp

type Dispatcher<S> = Dispatch<SetStateAction<S>>;

interface IProps {
  arr: string[] | null;
  setCurrentIndex: Dispatcher<number>;
  setArr: Dispatcher<string[] | null>
  setIsLoader: Dispatcher<boolean>
}


export const circleColor = (
  index: number,
  currentIndex: number,
  arr: Array<string | number>
) => {
  let length = arr.length - 1;
  if (currentIndex < index || currentIndex > length - index) {
    return ElementStates.Modified;
  }
  if (currentIndex === index || currentIndex === length - index) {
    return ElementStates.Changing;
  }
  return ElementStates.Default;
};

export const arrReverse = async ({arr, setCurrentIndex, setArr, setIsLoader}: IProps) => {
  const end = arr!.length - 1;
  const mid = Math.floor(arr!.length / 2);
  setCurrentIndex(0);
  for (let i = 0; i < mid; i++) {
    await delay(1000);
    [arr![i], arr![end - i]] = [arr![end - i], arr![i]];
    setCurrentIndex((i) => i + 1);
    setArr(arr);
  }
  setIsLoader(false);
};
