  //   setIsLoader(true);
  //   setIsDisabled(true);
  //   for (let i = 0; i < arr.length - 1; i++) {
  //     if (direction === Direction.Descending) {
  //       let maxInd = i;
  //       for (let j = i; j < arr.length; j++) {
  //         if (arr[maxInd] < arr[j]) {
  //           maxInd = j;
  //         }
  //       }
  //       if (i !== maxInd) {
  //         swap(arr, i, maxInd);
  //         await delay(1000);
  //         setArray([...arr]);
