  //         setArray([...arr]);
  //       }
  //     }
  //   }
  //   setIsLoader(false);
  //   setIsDisabled(false);
  // };

  const radioClickBub = () => {
    setIsCheckedBub(true);
    setMethod("bubble");
    setIsCheckedSel(false);
  };

  const radioClickSel = () => {
    setIsCheckedSel(true);
    setMethod("selection");
    setIsCheckedBub(false);
  };

  const radioClick = (method: string) => {
    method === "bubble" ? radioClickBub() : radioClickSel();
  };

  return (
    <SolutionLayout title="Сортировка массива">
      <div className={styles.container}>
        <div className={styles.navigation}>
          <div className={styles.navigation}>
            <RadioInput
              label="Выбор"
              defaultChecked
              disabled={isDisabled}
              extraClass={styles.navigationItem}
              onClick={() => radioClick("selection")}
              checked={isCheckedSel}
            />
            <RadioInput
              label="Пузырёк"
              disabled={isDisabled}
              extraClass={styles.navigationItem}
              onClick={() => radioClick("bubble")}
              checked={isCheckedBub}
            />
            <Button
              text="По возрастанию"
              isLoader={isLoader}
              extraClass={styles.navigationItem}
              onClick={
                method === "selection"
                  ? () => selectionSort(array, Direction.Ascending)
                  : () => bubbleSort(array, Direction.Ascending)
              }
            />
            <Button
              text="По убыванию"
              extraClass={styles.navigationItem}
              isLoader={isLoader}
              onClick={
                method === "selection"
                  ? () => selectionSort(array, Direction.Descending)
                  : () => bubbleSort(array, Direction.Descending)
              }
            />
          </div>
          <Button
            text="Новый массив"
            isLoader={isLoader}
            onClick={() => randomArr()}
          />
        </div>
        <div className={styles.result}>
          {array.map((el, index) => (
            <div key={index} className={styles.resultItem}>
              <Column index={el} />
            </div>
          ))}
        </div>
      </div>
    </SolutionLayout>
  );
};
