enum elementstates {
  DEFAULT = "default",
  CHANGING = "changing",
  MODIFIED = "modified",
}

export const circleColor = (
  index: number,
  currentIndex: number,
  arr: Array<string | number>
) => {
  let length = arr.length - 1;
  if (currentIndex < index || currentIndex > length - index) {
    return states.MODIFIED;
  }
  if (currentIndex === index || currentIndex === length - index) {
    return states.CHANGING;
  }
  return states.DEFAULT;
};
